CREATE TABLE seo (
	id SERIAL PRIMARY KEY,
	seo_id TEXT UNIQUE NOT NULL,
	keywords TEXT[],
	description TEXT
);

CREATE TABLE banners (
    id SERIAL PRIMARY KEY,
    seo_id TEXT REFERENCES seo(seo_id) ON DELETE RESTRICT,
    title TEXT NOT NULL,
    content TEXT NOT NULL,
    src_image TEXT NOT NULL
);

CREATE TABLE introduction (
    id SERIAL PRIMARY KEY,
    title TEXT NOT NULL,
    content TEXT NOT NULL,
	visions JSONB NOT NULL,
	missions JSONB NOT NULL,
	core_values JSONB NOT NULL,
	competitives JSONB NOT NULL,
    cover_url TEXT NOT NULL
);

CREATE TABLE founders (
    id SERIAL PRIMARY KEY,
    seo_id TEXT REFERENCES seo(seo_id) ON DELETE RESTRICT,
    name TEXT NOT NULL,
    avatar TEXT NOT NULL,
    job_title TEXT NOT NULL,
    about TEXT NOT NULL,
	socials JSONB NOT NULL
);

CREATE TABLE partners (
    id SERIAL PRIMARY KEY,
    seo_id TEXT REFERENCES seo(seo_id) ON DELETE RESTRICT,
    avatar TEXT NOT NULL,
    name TEXT NOT NULL,
    src TEXT
);

CREATE TABLE products (
    id SERIAL PRIMARY KEY,
    seo_id TEXT REFERENCES seo(seo_id) ON DELETE RESTRICT,
    title TEXT NOT NULL,
	contents JSONB NOT NULL,
    short_content TEXT NOT NULL,
    website TEXT NOT NULL,
	infos JSONB NOT NULL,
    info_url TEXT NOT NULL,
    cover_url TEXT NOT NULL,
    icon_url TEXT NOT NULL,
    icon_hover_url TEXT NOT NULL,
    phone_number TEXT NOT NULL,
    created_at DATE NOT NULL
);

CREATE TABLE our_contact (
    id SERIAL PRIMARY KEY,
    seo_id TEXT REFERENCES seo(seo_id) ON DELETE RESTRICT,
    address TEXT NOT NULL,
    email TEXT NOT NULL,
    phone_number TEXT NOT NULL,
	socials JSONB NOT NULL
);

CREATE TABLE categories (
    id SERIAL PRIMARY KEY,
    seo_id TEXT REFERENCES seo(seo_id) ON DELETE RESTRICT,
    title TEXT NOT NULL,
    content TEXT NOT NULL,
    position INT NOT NULL
);

CREATE TABLE news (
    id SERIAL PRIMARY KEY,
    seo_id TEXT REFERENCES seo(seo_id) ON DELETE RESTRICT,
    title TEXT NOT NULL,
    content TEXT NOT NULL,
    category_new_id INT REFERENCES categories(id) ON DELETE SET NULL,
    created_at DATE NOT NULL,
    cover_url TEXT NOT NULL,
    is_highlights BOOLEAN DEFAULT FALSE,
	num_view INT
);

CREATE TABLE jobs (
    id SERIAL PRIMARY KEY,
    seo_id TEXT REFERENCES seo(seo_id) ON DELETE RESTRICT,
    title TEXT NOT NULL,
    salary TEXT NOT NULL,
    level TEXT NOT NULL,
    career TEXT NOT NULL,
    job_description TEXT NOT NULL,
    job_requirements TEXT NOT NULL,
    job_benefits TEXT NOT NULL,
    work_experience TEXT NOT NULL,
    work_type TEXT NOT NULL,
    work_address TEXT NOT NULL,
    number_recruits INT NOT NULL,
    end_time TIMESTAMP NOT NULL,
    cover_url TEXT,
	info JSONB
);

CREATE TABLE contact_forms (
    id SERIAL PRIMARY KEY,
    full_name TEXT NOT NULL,
    phone_number TEXT NOT NULL,
    email TEXT NOT NULL,
    message TEXT,
    submitted_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE OR REPLACE FUNCTION update_submitted_at()
RETURNS TRIGGER AS $$
BEGIN
    NEW.submitted_at := CURRENT_TIMESTAMP;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER set_submitted_at
BEFORE INSERT ON contact_forms
FOR EACH ROW
EXECUTE FUNCTION update_submitted_at();

-- Function to ensure seo_id exists in the seo table
CREATE OR REPLACE FUNCTION ensure_seo_id_exists()
RETURNS TRIGGER AS $$
BEGIN
    -- Insert seo_id into seo table if it does not exist
    IF NOT EXISTS (SELECT 1 FROM seo WHERE seo_id = NEW.seo_id) THEN
        INSERT INTO seo (seo_id, keywords, description) VALUES (NEW.seo_id, NULL, NULL);
    END IF;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- Attach the trigger to relevant tables
CREATE TRIGGER ensure_seo_id_in_founders
BEFORE INSERT ON founders
FOR EACH ROW
EXECUTE FUNCTION ensure_seo_id_exists();

CREATE TRIGGER ensure_seo_id_in_partners
BEFORE INSERT ON partners
FOR EACH ROW
EXECUTE FUNCTION ensure_seo_id_exists();

CREATE TRIGGER ensure_seo_id_in_products
BEFORE INSERT ON products
FOR EACH ROW
EXECUTE FUNCTION ensure_seo_id_exists();

CREATE TRIGGER ensure_seo_id_in_data_footer
BEFORE INSERT ON data_footer
FOR EACH ROW
EXECUTE FUNCTION ensure_seo_id_exists();

CREATE TRIGGER ensure_seo_id_in_categories
BEFORE INSERT ON categories
FOR EACH ROW
EXECUTE FUNCTION ensure_seo_id_exists();

CREATE TRIGGER ensure_seo_id_in_news
BEFORE INSERT ON news
FOR EACH ROW
EXECUTE FUNCTION ensure_seo_id_exists();

CREATE TRIGGER ensure_seo_id_in_jobs
BEFORE INSERT ON jobs
FOR EACH ROW
EXECUTE FUNCTION ensure_seo_id_exists();

CREATE TRIGGER ensure_seo_id_in_banner
BEFORE INSERT ON banner
FOR EACH ROW
EXECUTE FUNCTION ensure_seo_id_exists();

INSERT INTO introduction (title, content, visions, missions, core_values, competitives, cover_url)
VALUES (
	'GIỚI THIỆU',
	'<p style=\"text-align:justify\">C&ocirc;ng ty MH Solution l&agrave; một đơn vị h&agrave;ng đầu chuy&ecirc;n cung cấp c&aacute;c dịch vụ v&agrave; giải ph&aacute;p về dữ liệu. Được th&agrave;nh lập v&agrave;o ng&agrave;y 05 th&aacute;ng 06 năm 2021, với sứ mệnh ti&ecirc;n phong trong việc khai th&aacute;c v&agrave; tận dụng gi&aacute; trị của dữ liệu, ch&uacute;ng t&ocirc;i cam kết mang đến cho kh&aacute;ch h&agrave;ng những giải ph&aacute;p th&ocirc;ng minh v&agrave; hiệu quả để họ c&oacute; thể tối ưu h&oacute;a tiềm năng dữ liệu của doanh nghiệp m&igrave;nh.</p>\n\n<p style=\"text-align:justify\">Với nền tảng c&ocirc;ng nghệ ti&ecirc;n tiến, đội ngũ chuy&ecirc;n gia gi&agrave;u kinh nghiệm v&agrave; hiểu biết s&acirc;u sắc về lĩnh vực kinh doanh, ch&uacute;ng t&ocirc;i tạo ra c&aacute;c giải ph&aacute;p t&ugrave;y chỉnh v&agrave; linh hoạt để đ&aacute;p ứng nhu cầu đa dạng v&agrave; t&igrave;nh h&igrave;nh cụ thể của từng kh&aacute;ch h&agrave;ng. Đặc biệt, MH Solution lu&ocirc;n đồng h&agrave;nh c&ugrave;ng kh&aacute;ch h&agrave;ng trong suốt qu&aacute; tr&igrave;nh triển khai v&agrave; cung cấp hỗ trợ chuy&ecirc;n nghiệp sau khi dự &aacute;n ho&agrave;n th&agrave;nh.</p>\n\n<p style=\"text-align:justify\">MH Solution lu&ocirc;n đặt lợi &iacute;ch của kh&aacute;ch h&agrave;ng l&ecirc;n h&agrave;ng đầu. Ch&uacute;ng t&ocirc;i hiểu rằng dữ liệu l&agrave; t&agrave;i sản qu&yacute; gi&aacute; v&agrave; khai th&aacute;c hiệu quả dữ liệu c&oacute; thể mang lại lợi &iacute;ch lớn cho doanh nghiệp. V&igrave; vậy, ch&uacute;ng t&ocirc;i cam kết cung cấp c&aacute;c giải ph&aacute;p dữ liệu tối ưu, an to&agrave;n v&agrave; bảo mật để kh&aacute;ch h&agrave;ng c&oacute; thể tự tin trong việc sử dụng dữ liệu của m&igrave;nh m&agrave; kh&ocirc;ng phải lo lắng về rủi ro.</p>\n\n<p style=\"text-align:justify\">MH Solution kh&ocirc;ng ngừng nỗ lực n&acirc;ng cao chất lượng dịch vụ v&agrave; mở rộng phạm vi hoạt động để đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng cao về dữ liệu của kh&aacute;ch h&agrave;ng. H&atilde;y đồng h&agrave;nh c&ugrave;ng MH Solution để kh&aacute;m ph&aacute; tiềm năng của dữ liệu v&agrave; đạt được th&agrave;nh c&ocirc;ng bền vững trong thời đại số h&oacute;a.</p>\n',
	'[{"title": "tầm nhìn", "content": "TRỞ THÀNH MỘT TẬP ĐOÀN CÔNG NGHỆ TRONG TOP 30 VIỆT NAM VÀO NĂM 2030."}]'::JSONB,
	'[{"title": "Sứ mệnh", "content": "DẪN ĐẦU CÁC XU HƯỚNG CÔNG NGHỆ MỚI ĐỂ MANG TỚI CÁC GIẢI PHÁP CÔNG NGHỆ HIỆU QUẢ CHO KHÁCH HÀNG"}]'::JSONB,
	'[{"title": "giá trị cốt lõi", "content": "Chúng tôi tập trung vào phát triển con người và văn hóa doanh nghiệp. Với 5 giá trị cốt lõi, nhân sự của chúng tôi luôn luôn phát triển và hoàn thiện với tinh thần: ''Sáng tạo chủ động, phát huy tính đồng đội, trách nhiệm với bản thân, công ty và hướng khách hàng.''"}]'::JSONB,
	'[{"title": "CÔNG NGHỆ CAO", "content": "Giải quyết các thách thức trong kinh doanh của bạn với công nghệ Big Data, Data Mining, AI, RPA ,..."}, {"title": "GIẢI PHÁP CHUYÊN BIỆT", "content": "Các giải pháp được đóng gói đảm bảo tính tùy chỉnh cao, phù hợp với nghiệp vụ và đặc thù của từng tổ chức doanh nghiệp."}, {"title": "CHUYÊN GIA ĐỒNG HÀNH", "content": "Các chuyên gia của chúng tôi cam kết đồng hành để cùng phân tích, xây dựng và áp dụng hiệu quả các giải pháp vào tổ chức doanh nghiệp."}]'::JSONB,
	'https://cdn.mhdigital.vn/mhsolution/P7LMVxEWLbYD.jpeg'
);

